import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [books, setBooks] = useState([]);
  const [formData, setFormData] = useState({ title: "", author: "", genre: "", year: "" });
  const [editingBook, setEditingBook] = useState(null);

  const fetchBooks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/books");
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    if (editingBook) {
      setFormData({
        title: editingBook.title,
        author: editingBook.author,
        genre: editingBook.genre,
        year: editingBook.year
      });
    }
  }, [editingBook]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleAddOrUpdate = async (e) => {
    e.preventDefault();
    try {
      if (editingBook) {
        await axios.put(`http://localhost:5000/api/books/${editingBook._id}`, formData);
        setEditingBook(null);
      } else {
        await axios.post("http://localhost:5000/api/books", formData);
      }
      setFormData({ title: "", author: "", genre: "", year: "" });
      fetchBooks();
    } catch (err) {
      console.error("Error saving book:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error("Error deleting book:", err);
    }
  };

  return (
    <div className="App">
      <h1>ðŸ“š BookVault</h1>

      {editingBook && <h2>Editing: {editingBook.title}</h2>}

      <form onSubmit={handleAddOrUpdate}>
        <input name="title" value={formData.title} onChange={handleChange} placeholder="Title" required />
        <input name="author" value={formData.author} onChange={handleChange} placeholder="Author" required />
        <input name="genre" value={formData.genre} onChange={handleChange} placeholder="Genre" />
        <input name="year" value={formData.year} onChange={handleChange} placeholder="Year" type="number" />
        <button type="submit">{editingBook ? "Update" : "Add"} Book</button>
      </form>

      <ul>
        {books.map(book => (
          <li key={book._id}>
            <strong>{book.title}</strong> by {book.author} ({book.year}) [{book.genre}]
            <button onClick={() => setEditingBook(book)}>Edit</button>
            <button onClick={() => handleDelete(book._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
