import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '', publishedYear: '' });
  const [editingId, setEditingId] = useState(null);

  const fetchBooks = async () => {
    const res = await axios.get("http://localhost:5000/api/books");
    setBooks(res.data);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAddOrUpdate = async () => {
    if (editingId) {
      await axios.put(`http://localhost:5000/api/books/${editingId}`, form);
      setEditingId(null);
    } else {
      await axios.post("http://localhost:5000/api/books", form);
    }
    setForm({ title: '', author: '', genre: '', publishedYear: '' });
    fetchBooks();
  };

  const handleEdit = (book) => {
    setForm({
      title: book.title,
      author: book.author,
      genre: book.genre,
      publishedYear: book.publishedYear,
    });
    setEditingId(book._id);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/books/${id}`);
    fetchBooks();
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>üìö BookVault</h1>
      <input name="title" placeholder="Title" value={form.title} onChange={handleChange} />
      <input name="author" placeholder="Author" value={form.author} onChange={handleChange} />
      <input name="genre" placeholder="Genre" value={form.genre} onChange={handleChange} />
      <input name="publishedYear" placeholder="Year" type="number" value={form.publishedYear} onChange={handleChange} />
      <button onClick={handleAddOrUpdate}>
        {editingId ? "Update Book" : "Add Book"}
      </button>

      <ul>
        {books.map(book => (
          <li key={book._id}>
            <strong>{book.title}</strong> by {book.author} ({book.publishedYear}) [{book.genre}]
            <button onClick={() => handleEdit(book)}>‚úèÔ∏è</button>
            <button onClick={() => handleDelete(book._id)}>‚ùå</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
